import React, { useState, useEffect } from 'react';
import Image from '../images/icon-dice.svg';
import Pattern from '../images/pattern-divider-mobile.svg'

const Generator = () => {
    const [advice, setAdvice] = useState("");
    const [adviceNumber, setAdviceNumber] = useState(0);

    useEffect(() => {
        const fetchAdvice = async () => {
            const randomAdviceId = Math.floor(Math.random() * 401) + 1;
            const response = await fetch(`https://api.adviceslip.com/advice/${randomAdviceId}`);
            const data = await response.json();
            setAdvice(data.slip.advice);
            setAdviceNumber(randomAdviceId);
        }
        fetchAdvice();
    }, []);

    const handleClick = async () => {
        const adviceId = Math.floor(Math.random() * 401) + 1;
        const response = await fetch(`https://api.adviceslip.com/advice/${adviceId}`);
        const data = await response.json();
        setAdvice(data.slip.advice);
        setAdviceNumber(adviceId);
    }

    return (
        <div id="advice-box">
            <h1>ADVICE #{adviceNumber}</h1>
            <p id="text">{advice}</p>
            <img id="pattern" src={Pattern} alt="Pattern Divider"/>
            <button type="button" id="btn" onClick={handleClick}><img id="dice" src={Image} alt="Icon Dice"/></button>
        </div>
    )
}

export default Generator
